"use strict";document.addEventListener("DOMContentLoaded",(function(){const e=document.querySelector(".container"),t=document.querySelector(".image");document.body.onmousemove=e=>{let o=e.pageX,c=e.pageY;t.style.top=c+"px",t.style.left=o+"px",t.style.display="block"},e.querySelectorAll(".title").forEach(e=>{e.onmousemove=()=>{t.src=e.getAttribute("data-src")+".jpg",t.previousElementSibling.srcset=e.getAttribute("data-src")+".webp"}})}));